/* Just onReady */
$(document).ready(function() {
    /* Invert display status on Click */
    $('.redSheepNaviBTN').click(function() {
        var vis = $('.redsheepNaviSlide').css('display');

        if (vis === 'none') {
            $('.redsheepNaviSlide').fadeIn();
        } else {
            $('.redsheepNaviSlide').fadeOut();
        }
    });
});

/* Close navi by fadeOut */
function slideClose() {
    $('.redsheepNaviSlide').fadeOut();
}

function invertSub(menu) {
    
    $('.redSheepNaviSubMenu').fadeOut();
    
    var selector = '.' + menu;
    var vis = $(selector).css('display');

    if (vis === 'none') {
        $(selector).fadeIn();
    } else {
        $(selector).fadeOut();
    }
}